import React, {Component} from "react"
import {Card, Icon, Avatar, BackTop, List} from 'antd';
import './default.less'
import axios from 'axios'
import browser from "less/lib/less-browser/browser";

const {Meta} = Card;


export default class Default extends Component {

    state = {
        articleList: [],
        recommendedList: []
    };

    componentDidMount() {
        axios.get('http://com.cn/getArticle')
            .then(data => {
                this.setState({
                    articleList: data.data
                })
            })
            .catch(err => Promise.reject(err));

        axios.get('http://com.cn/getRecommendData')
            .then(data => {
                  this.setState({
                      recommendedList:data.data
                  })
            })
    }

    render() {
        const data = this.state.recommendedList;
        const {articleList} = this.state;
        return (
            <div className={'default'}>
                <BackTop/>
                {/* 左侧导航区域 */}
                <div className="leftContent">
                    {
                        articleList.map(item => {
                            return (
                                <Card
                                    key={item.key}
                                    style={{width: "100%", marginBottom: 18}}
                                    cover={
                                        <img
                                            style={{height: 280}}
                                            alt="example"

                                            src={item.cover}
                                        />
                                    }
                                    actions={[
                                        <Icon type="eye" key={'eye'}/>,
                                        <Icon type="like"/>,
                                        <Icon type="message" key={'message'}/>,
                                        <Icon type="edit" key="edit"/>,
                                    ]}
                                >
                                    <Meta
                                        avatar={<Avatar
                                            src="data:image/jpeg;base64,"/>}
                                        title={item.title}
                                        description={item.description}
                                    />
                                </Card>
                            )
                        })
                    }
                </div>

                {/*右侧Panel区域*/}
                <div className="rightPanel">
                    <Card
                        cover={
                            <img
                                style={{height: 280}}
                                alt="example"
                                src="data:image/jpeg;base64,"
                            />
                        }

                        actions={[
                            <Icon type="eye" key={'eye'}/>,
                            <Icon type="like"/>,
                            <Icon type="message" key={'message'}/>,
                            <Icon type="edit" key="edit"/>,
                        ]}
                    >
                        {/*  右侧导航区域  */}

                    </Card>
                    <div className={"RecommendedToday"}>
                        <p className="title">
                            <Icon type="like"/> <span>强烈推荐</span>
                        </p>

                        <List
                            itemLayout="horizontal"
                            dataSource={data}
                            renderItem={item => (
                                <List.Item>
                                    <List.Item.Meta
                                        title={<a href="https://ant.design">{item.title}</a>}
                                    />
                                </List.Item>
                            )}
                        />
                    </div>
                </div>
            </div>
        )
    }
}